package com.intuit.moet;/** * Common utility to be used by all devices. * @author eong * */public class Util {		public static String amountEntry(String amount)	{		String[] amountArray = String.valueOf(amount).split("\\.");		StringBuffer results = new StringBuffer(amountArray[0]);				if (amountArray.length < 2)			return results.toString();				String cents = amountArray[1];		results.append(cents);		if (cents.length() == 1)			results.append("0");				return results.toString();	}		/**	 * Converts percentages to absolute co-ordinates	 * @param percentCoord percent coorindate	 * @param type x or y	 * @param settings contains resolution and output directories	 * @return	 */	public static int absCoordinates(String percentCoord, String type, Settings settings)	{		int coordValue = 0;		int resolution = 0;		if (percentCoord.endsWith("%"))		{			coordValue = Integer.parseInt(percentCoord.substring(0, percentCoord.length() - 1));			if (settings.resolution != null)			{				if (type.contains("x"))										resolution = settings.resX;				else					resolution = settings.resY;			}			else			{				if (type.contains("x"))					resolution = 480;				else					resolution = 800;			}					return (coordValue * resolution) / 100;		}		else			return coordValue;	}}